import React, { useState } from 'react';
import axios from 'axios';

function LawSearch() {
  const [query, setQuery] = useState('');
  const [aiResults, setAiResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [buttonDisabled, setButtonDisabled] = useState(false);

  const handleSearch = async () => {
    setLoading(true);
    setButtonDisabled(true);
    try {
      const response = await axios.get(`/api/openai-laws?query=${encodeURIComponent(query)}`);
      setAiResults(response.data);
    } catch (err) {
      alert('Failed to fetch OpenAI results');
    } finally {
      setLoading(false);
      setButtonDisabled(false);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Search US Cybersecurity Laws</h2>
      <div className="flex mb-4">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Enter a law topic..."
          className="border p-2 mr-2 flex-grow"
        />
        <button
          onClick={handleSearch}
          disabled={buttonDisabled}
          className={`px-4 py-2 rounded text-white ${buttonDisabled ? 'bg-gray-400' : 'bg-blue-600 hover:bg-blue-700'}`}
        >
          Search
        </button>
      </div>

      {loading && <p className="text-xl font-semibold text-center mb-4">Loading Results...</p>}

      <div>
        <h3 className="text-xl font-semibold mb-2">OpenAI Suggestions</h3>
        {aiResults.length === 0 ? (
          <p>No suggestions found.</p>
        ) : (
          aiResults.map((item, index) => (
            <div key={index} className="border p-4 mb-2 rounded">
              <h4 className="font-bold text-lg">{item.title}</h4>
              <p className="mb-1">{item.description}</p>
              <a href={item.url} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">
                {item.url}
              </a>
              <p className="italic text-sm mt-1">{item.citation}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default LawSearch;
